private void CALLBACK_Portal_InitializeAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortal myArcGISPortal, System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortal.InitializeAsync Method. 

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Get the ArcGISPortalInfo object from ArcGISPortal. 
    ESRI.ArcGIS.Client.Portal.ArcGISPortalInfo myArcGISPortalInfo = myArcGISPortal.ArcGISPortalInfo;

    // Get the ArcGISPortalInfo.PortalHostname information.
    string myPortalHostName = "";
    if (myArcGISPortalInfo != null)
    {
      myPortalHostName = myArcGISPortalInfo.PortalHostname;
    }

    // Get the ArcGISPortal.CurrentUser object. Initialize some string variables for the username and email of the
    // CurrentUser. If we have a valid CurrentUser get that information. If the user tries to get information using
    // anonymous access the default string values of "[NOT AVAILABLE]" are provided.
    ESRI.ArcGIS.Client.Portal.ArcGISPortalUser myCurrentUser = myArcGISPortal.CurrentUser;
    string myUserName = "[NOT AVAILABLE]";
    string myEmail = "[NOT AVAILABLE]";
    if (myCurrentUser != null)
    {
      myUserName = myCurrentUser.UserName;
      myEmail = myCurrentUser.Email;
    }

    // Get the CurrentVerion of the AGOL/Portal server.
    string myCurrentVersion = myArcGISPortal.CurrentVersion;

    // Get the Url of the AGOL/Portal server.
    string myUrl = myArcGISPortal.Url;

    // Create a StringBuilder class to hold information obtained from the AGOL/Portal service.
    System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();
    myStringBuilder.Append("ArcGISPortalInfo.PortalHostName: " + myPortalHostName + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortal.CurrentVersion: " + myCurrentVersion + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortal.Url: " + myUrl + Environment.NewLine);
    myStringBuilder.Append("CurrentUser.UserName: " + myUserName + Environment.NewLine);
    myStringBuilder.Append("CurrentUser.Email: " + myEmail + Environment.NewLine);

    // Display the information about the AGOL/Portal server to the user.
    TextBlock_GenericInfo.Text = myStringBuilder.ToString();

    //----------------------------------------------------------------
    // Now that the ArcGISPortal.InitializeAsync has successfully been invoked, we can now get more detailed information
    // about a specific ArcGISPortalGroup. 

    // Create a new SearchParameters object.
    ESRI.ArcGIS.Client.Portal.SearchParameters mySearchParameters = new ESRI.ArcGIS.Client.Portal.SearchParameters();

    // Get the Id value for the ArcGISPortal Group.
    string myArcGISPortalGroupId = TextBox_SearchGroupId.Text;

    // Add the search Id of the ArcGISPortalGroup to the SearchParameters.
    mySearchParameters.QueryString = myArcGISPortalGroupId;

    // Create a new ArcGISPortalGroup using the ArcGISPortal object in the constructor.
    ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup myArcGISPortalGroup = new ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup(myArcGISPortal);

    // Use the ArcGISPortalGroup object to access Group information in AGOL/Portal services. This will cause an asynchronous call to the 
    // sub-routine called CALLBACK_ArcGISPortalGroup_InitializeAsync. 
    myArcGISPortalGroup.InitializeAsync(myArcGISPortalGroupId, CALLBACK_ArcGISPortalGroup_InitializeAsync);
  }
  else
  {
    // There was some problem with the ArcGISPortal.InitializeAsync call. Display the message to the user.
    MessageBox.Show("Failed to initialize" + ex.Message.ToString());
  }
}

private void CALLBACK_ArcGISPortalGroup_InitializeAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup myArcGISPortalGroup, System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortalGroup.InitializeAsync Method. 

  // Ensure that an ArcGISPortalGroup was found.
  if (myArcGISPortalGroup.IsInitialized == true)
  {
    // A valid ArcGISPortalGroup was returned.

    // Get various ArcGISPortalGroup Property information. Like: the image logo for the Group, the owner of the Group, 
    // the access level of the Group (Private, Public, etc.), the title of the Group, and the creation date of the 
    // Group. 
    Uri myImageUri = myArcGISPortalGroup.ThumbnailUri;
    string myOwner = myArcGISPortalGroup.Owner;
    ESRI.ArcGIS.Client.Portal.PortalAccess myAccess = (ESRI.ArcGIS.Client.Portal.PortalAccess)myArcGISPortalGroup.Access;
    string myTitle = myArcGISPortalGroup.Title;
    DateTime myCreationDate = myArcGISPortalGroup.CreationDate;

    // Create a new StackPanel to house the information that will be displayed to the user about the Group found.
    StackPanel myStackPanel = new StackPanel();
    myStackPanel.Orientation = Orientation.Vertical;

    if (myImageUri != null)
    {
      // If the Group has an image logo, display it to the user.

      // Create a new Image object, define its size and set the Uri of the Image to that for the Group. 
      Image myImage = new Image();
      myImage.Width = 300;
      myImage.Height = 150;
      myImage.Source = new System.Windows.Media.Imaging.BitmapImage(myImageUri); // Silverlight has issues with .gif format images!

      // Display the image on the left in the ListBox.
      StackPanel myStackPanel2 = new StackPanel();
      myStackPanel2.Orientation = Orientation.Horizontal;
      myStackPanel2.HorizontalAlignment = System.Windows.HorizontalAlignment.Left;
      myStackPanel2.Children.Add(myImage);
      myStackPanel.Children.Add(myStackPanel2);
    }
    else
    {
      // The Group does not have an image thumbnail defined. 

      // Inform the use that no image thumbnail is available for display. 
      TextBlock myTextBlock_ArcGISPortalGroup_ThumbnailImage = new TextBlock();
      myTextBlock_ArcGISPortalGroup_ThumbnailImage.Text = "[NO THUMBNAIL IMAGE AVAILABLE]";
      myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_ThumbnailImage);
    }

    // Display the owner of the Group.
    TextBlock myTextBlock_ArcGISPortalGroup_Owner = new TextBlock();
    myTextBlock_ArcGISPortalGroup_Owner.Text = "ArcGISPortalGroup.Owner: " + myOwner.ToString();
    myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Owner);

    // Display the AGOL/Portal Group access level (i.e. Public, Private, etc.).
    TextBlock myTextBlock_ArcGISPortalGroup_Access = new TextBlock();
    myTextBlock_ArcGISPortalGroup_Access.Text = "ArcGISPortalGroup.Access: " + myAccess.ToString();
    myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Access);

    // Display the title of the Group.
    TextBlock myTextBlock_ArcGISPortalGroup_Title = new TextBlock();
    myTextBlock_ArcGISPortalGroup_Title.Text = "ArcGISPortalGroup.Title: " + myTitle.ToString();
    myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Title);

    // Display the creation date of the Group.
    TextBlock myTextBlock_ArcGISPortalGroup_CreationDate = new TextBlock();
    myTextBlock_ArcGISPortalGroup_CreationDate.Text = "ArcGISPortalGroup.CreationDate: " + myCreationDate.ToString();
    myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_CreationDate);

    // Add the various display objects about the Group in the ListBox.
    ListBox_GroupInfo.Items.Add(myStackPanel);

    // Use the ArcGISPortalGroup object to access Group user information in AGOL/Portal services. This will cause an 
    // asynchronous call to the sub-routine called CALLBACK_ArcGISPortalGroup_GetGroupUsersAsync. 
    myArcGISPortalGroup.GetGroupUsersAsync(CALLBACK_ArcGISPortalGroup_GetGroupUsersAsync);
  }
  else
  {
    // No ArcGISPortalGroup was found.
    ListBox_GroupInfo.Items.Add("No ArcGISPortalGroup found!");
  }
}

private void CALLBACK_ArcGISPortalGroup_GetGroupUsersAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortalGroupUsers myArcGISPortalGroupUsers, System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortalGroup.GetGroupUsersAsync Method. 

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Get various ArcGISPortalGroupUsers Property information. Like: the administrators of the Group, the owner (aka. creator)
    // of the Group, and the usernames of those who are in the Group.
    System.Collections.Generic.IEnumerable<string> myAdmins = myArcGISPortalGroupUsers.Admins;
    string myOwner = myArcGISPortalGroupUsers.Owner;
    System.Collections.Generic.IEnumerable<string> myUsers = myArcGISPortalGroupUsers.Users;

    // Create a StringBuilder class to hold information obtained from the AGOL/Portal service.
    System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();

    // Loop through and add the administrators of the Group to the StringBuilder object.
    int myNumberOfAdmins = myAdmins.Count();
    myStringBuilder.Append("Number of Admininstators: " + myNumberOfAdmins.ToString() + Environment.NewLine);
    foreach (string oneAdmin in myAdmins)
    {
      myStringBuilder.Append("Administrator username: " + oneAdmin + Environment.NewLine);
    }

    // Add the owner (aka. creator) of the Group to the StringBuilder object.
    myStringBuilder.Append(Environment.NewLine);
    myStringBuilder.Append("Group owner username: " + myOwner + Environment.NewLine);
    myStringBuilder.Append(Environment.NewLine);

    // Loop through and add the usernames of the those who are in the Group to the StringBuilder object.
    int myNumberOfUsers = myUsers.Count();
    myStringBuilder.Append("Number of Users: " + myNumberOfUsers.ToString() + Environment.NewLine);
    foreach (string oneUser in myUsers)
    {
    myStringBuilder.Append("UserName: " + oneUser + Environment.NewLine);
    }

    // Add the StringBuilder content to the ListBox.
    ListBox_GroupInfo.Items.Add(myStringBuilder.ToString());
  }
  else
  {
    // There was some problem with the ArcGISPortalGroup.GetGroupUsersAsync call. Display the message to the user.
    MessageBox.Show("Failed to initialize" + ex.Message.ToString());
  }
}

private void CheckBox_UseAnonymousAccess_Click(object sender, System.Windows.RoutedEventArgs e)
{
  // This sub-routine execute when the user checks on/off the ability to get information about AGOL/Portal 
  // using anonymous access or via the IdentityManager with a username/password for secured web services.

  if (CheckBox_UseAnonymousAccess.IsChecked == true)
  {
    // The user wants to have anonymous access to get AGOL/Portal information.
    // Hide the username/password controls.

    Label_Password.Visibility = System.Windows.Visibility.Collapsed;
    Label_UserName.Visibility = System.Windows.Visibility.Collapsed;
    TextBox_Password.Visibility = System.Windows.Visibility.Collapsed;
    TextBox_UserName.Visibility = System.Windows.Visibility.Collapsed;
  }
  else
  {
    // The user wants to have IdentityManager with a username/password access to get AGOL/Portal information.
    // Show the username/password controls.

    Label_Password.Visibility = System.Windows.Visibility.Visible;
    Label_UserName.Visibility = System.Windows.Visibility.Visible;
    TextBox_Password.Visibility = System.Windows.Visibility.Visible;
    TextBox_UserName.Visibility = System.Windows.Visibility.Visible;
  }
}

