    ESRI.ArcGIS.Client.IdentityManager.Current.GenerateCredentialAsync(_DefaultServerUrl, _Username, _Password, CALLBACK_IdentityManager_GenerateCredentialAsync);
  }

  private void CALLBACK_IdentityManager_ChallengeMethod(string url, Action<ESRI.ArcGIS.Client.IdentityManager.Credential, Exception> callback, ESRI.ArcGIS.Client.IdentityManager.GenerateTokenOptions options)
  {
    // Activate the IdentityManager.
    callback(null, new NotImplementedException());
  }

  private void CALLBACK_IdentityManager_GenerateCredentialAsync(ESRI.ArcGIS.Client.IdentityManager.Credential crd, System.Exception ex)
  {
    // The callback that obtains the credential object.

    if (crd != null)
    {
      // We got a valid credential user token object.

      // Add the credential user token to the IdentityManager for accessing resources. 
      ESRI.ArcGIS.Client.IdentityManager.Current.AddCredential(crd);

      // Begin the process of accessing ArcGIS Online/ArcGIS Portal using the user specified Member variables defined above. 
      _MyArcGISPortal.InitializeAsync(_DefaultServerUrl, CALLBACK_ArcGISPortal_InitializeAsync);
    }
    else
    {
      // We had a problem generating the credentialed user token. Notify the user of the problem.
      MessageBox.Show("Could not log in. Please check credentials.");
    }
  }

  private void CALLBACK_ArcGISPortal_InitializeAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortal credential, System.Exception exception)
  {
    // The callback that establishes valid communication with ArcGIS Online/ArcGIS Portal.

    if (credential != null && credential.CurrentUser != null)
    {
      // We have valid credentials.

      // Set the Member variable for the ArcGISPortalUser to the user credential that was found using the IdentityManager.
      _MyArcGISPortalUser = credential.CurrentUser;

      // Display the FullName of the credentialed user in the TextBlock.
      TextBlock_CurrentUser.Text = string.Format(credential.CurrentUser.FullName);

      // Clear out any existing ArcGISPortalGroupExtended objects from the ListBox.
      ListBox_Results.ItemsSource = null;

      // Set the ArcGISPortal.Url to the user defined Member variable.
      if (string.IsNullOrEmpty(_MyArcGISPortal.Url))
      {
        _MyArcGISPortal.Url = _DefaultServerUrl;
      }

     // Get the ArcGISPortalItems for the credentialed user.
    _MyArcGISPortalUser.GetItemsAsync(CALLBACK_ArcGISPortalUser_GetItemsAsync);

    // Get the Groups that an ArcGISPortalUser belongs to.
    _MyArcGISPortalUser.GetGroupsAsync(CALLBACK_ArcGISPortalUser_GetGroupsAsync);
    }
  }

  private void CALLBACK_ArcGISPortalUser_GetGroupsAsync(IEnumerable<ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup> grps, System.Exception err)
  {
    // Add the Groups the credentialed user has to the ArcGISPortalGroupExtended collection.
    _MyListOfArcGISPortalGroupExtended.AddRange(
      from g in grps
      select new ArcGISPortalGroupExtended(g));

    // Add the ArcGISPortalGroupExtended objects to the ListBox.
    ListBox_GroupsOwned.ItemsSource = _MyListOfArcGISPortalGroupExtended;
  }

  private void CALLBACK_ArcGISPortalUser_GetItemsAsync(System.Collections.Generic.IEnumerable<ESRI.ArcGIS.Client.Portal.ArcGISPortalItem> userItems, System.Exception ex)
  {
    // Add the ArcGISPortalItems to the ListBox.
    ListBox_Results.ItemsSource = userItems;
  }

  private void Button_ArcGISPortalItem_ShareAsync_Ver1_Click(object sender, System.Windows.RoutedEventArgs e)
  {
    // Executes when the user clicks the Button. Changes the access level permissions for the selected ArcGISPortalItem.

    // Get the ArcGISPortalItem from the ListBox.
    _MyArcGISPortalItem = ListBox_Results.SelectedItem as ESRI.ArcGIS.Client.Portal.ArcGISPortalItem;

    // Warn the user that before they can change an access level on an ArcGISPortalItem, that they must first select it.
    if (_MyArcGISPortalItem == null)
    {
      MessageBox.Show("Select an ArcGISPortalItem from the ListBox before trying to change access level permissions.");
      return;
    }

    // Get the list of all the Groups the user can set for an ArcGISPortalItem.
    System.Collections.Generic.List<string> my_GroupIDs_ALL = new System.Collections.Generic.List<string>();
    foreach (ArcGISPortalGroupExtended myArcGISPortalGroupExtended in _MyListOfArcGISPortalGroupExtended)
    {
      ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup myArcGISPortalGroup = myArcGISPortalGroupExtended.Item;
      my_GroupIDs_ALL.Add(myArcGISPortalGroup.Id);
    }

    //------------------------------------------
    if (RadioButton_Public.IsChecked == true)
    {
      // The user has chosen to make the ArcGISPortalItem Public. In this code example we will make sure that all other PortalAccess
      // levels are turned off and clear out any Groups to which the ArcGISPortalItem may belong to.
      // NOTE: it is possible to have multiple PortalAccess levels and Groups specified, it is just not shown in this example.
      _MyArcGISPortalItem.UnshareAsync(my_GroupIDs_ALL, (result, err) =>
      {
        // Display any error messages if any.
        if (err != null)
        {
          MessageBox.Show(err.Message);
        }

        // Now set the PortalAccess level for everyone (i.e. Public).
        _MyArcGISPortalItem.ShareAsync(null, true, false, CALLBACK_ArcGISPortalItem_ShareAsync);
      });

    }
    //------------------------------------------
    if (RadioButton_Organization.IsChecked == true)
    {
      // The user has chosen to make the ArcGISPortalItem open to the Organization. In this code example we will make sure that all other 
      // PortalAccess levels are turned off and clear out any Groups to which the ArcGISPortalItem may belong to.
      // NOTE: it is possible to have multiple PortalAccess levels and Groups specified, it is just not shown in this example.
      _MyArcGISPortalItem.UnshareAsync(my_GroupIDs_ALL, (result, err) =>
      {
        // Display any error messages if any.
        if (err != null)
        {
          MessageBox.Show(err.Message);
        }

        // Now set the PortalAccess level for all users in the Organization.
        _MyArcGISPortalItem.ShareAsync(null, false, true, CALLBACK_ArcGISPortalItem_ShareAsync);
      });
    }
    //------------------------------------------
    if (RadioButton_Private.IsChecked == true)
    {
      // The user has chosen to keep the ArcGISPortalItem Private (i.e. just to the credentialed user). In this code example we will make 
      // sure that all other PortalAccess levels are turned off and clear out any Groups to which the ArcGISPortalItem may belong to.
      // NOTE: it is possible to have multiple PortalAccess levels and Groups specified, it is just not shown in this example.
      _MyArcGISPortalItem.UnshareAsync(my_GroupIDs_ALL, (result, err) =>
      {
        // Display any error messages if any.
        if (err != null)
        {
          MessageBox.Show(err.Message);
        }

        // Now set the PortalAccess level to be just the credentialed user (i.e. Private).
        _MyArcGISPortalItem.ShareAsync(null, false, false, CALLBACK_ArcGISPortalItem_ShareAsync);
      });
    }
    //------------------------------------------
    if (RadioButton_Shared.IsChecked == true)
    {
      // The user has chosen to share the ArcGISPortalItem with one or more Groups. Get the selected Groups from the CheckBoxes
      // that were checked On and put the Group ID values in a List.
      // NOTE: it is possible to have multiple PortalAccess levels and Groups specified, it is just not shown in this example.

      System.Collections.Generic.List<string> mySelectedGroupIDs = new System.Collections.Generic.List<string>();
      foreach (ArcGISPortalGroupExtended myArcGISPortalGroupExtended in _MyListOfArcGISPortalGroupExtended)
      {
        if (myArcGISPortalGroupExtended.Selected)
        {
          ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup myArcGISPortalGroup = myArcGISPortalGroupExtended.Item;
          mySelectedGroupIDs.Add(myArcGISPortalGroup.Id);
        }
      }

      // Make sure that at least one Group is chosen.
      if (mySelectedGroupIDs.Count == 0)
      {
        MessageBox.Show("At least one Group must be checked on  in order to set Shared access level permissions.");
        return;
      }

      // Now set the PortalAccess level to be Shared and specify which Groups will have access to the ArcGISPortalItem.
      _MyArcGISPortalItem.ShareAsync(mySelectedGroupIDs, false, false, CALLBACK_ArcGISPortalItem_ShareAsync);
    }
    //------------------------------------------
  }

  private void CALLBACK_ArcGISPortalItem_ShareAsync(System.Collections.Generic.IEnumerable<string> result, System.Exception err)
  {
    // This function executes when the changing of the ArcGISPortalItem access level changes.

    // Display an error messages.
    if (err != null)
    {
      MessageBox.Show(err.Message);
    }
    else
    {

      // Get the selected ArcGISPortalItem in the ListBox.
      _MyArcGISPortalItem = ListBox_Results.SelectedItem as ESRI.ArcGIS.Client.Portal.ArcGISPortalItem;

      // Make sure we have a valid ArcGISPortalItem. 
      if (_MyArcGISPortalItem == null)
      {
        return;
      }

      // Call the ArcGISPortalItem.GetSharingInfoAsync which will update the RadioButton and CheckBox controls with the most
      // current access level information.
      _MyArcGISPortalItem.GetSharingInfoAsync(CALLBACK_ArcGISPortalItem_GetSharingInfoAsync);
    }
  }

  private void CALLBACK_ArcGISPortalItem_GetSharingInfoAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortalSharingInfo sharinginfo, System.Exception ex)
  {
    // This function will display the access level and other information about the ArcGISPortalItem.

    // -----------------------------------
    // Display various information in the TextBox. Add more as you feel.

    System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();

    ESRI.ArcGIS.Client.Portal.ArcGISPortalItem myPortalItem = sharinginfo.ArcGISPortalItem;
    myStringBuilder.Append("ArcGISPortalItem.Title = " + myPortalItem.Title + Environment.NewLine);

    myStringBuilder.Append("ArcGISPortalItem.AvgRating: " + myPortalItem.AvgRating.ToString() + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortalItem.Type: " + myPortalItem.Type.ToString() + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortalItem.Snippet: " + myPortalItem.Snippet + Environment.NewLine);

    myStringBuilder.Append("---------------------------------------------------" + Environment.NewLine);

    ESRI.ArcGIS.Client.Portal.PortalAccess myPortalAccess = sharinginfo.Access;
    myStringBuilder.Append("ArcGISPortalItem.PortalAccess = " + myPortalAccess.ToString() + Environment.NewLine);

    System.Collections.Generic.IEnumerable<string> myGroupIds = sharinginfo.GroupIds;
    foreach (string oneGroupId in myGroupIds)
    {
      myStringBuilder.Append("ArcGISPortalItem.GroupId = " + oneGroupId + Environment.NewLine);
    }

    TextBox1.Text = myStringBuilder.ToString();

    // -----------------------------------

    // Update the RadioButtons that represent the ArcGISPortalItem PortalAccess Enumerations. Also check on any
    // Group CheckBoxes that are available for the Shared PortalAccess level.

    if (_MyArcGISPortalItem.Owner != null && _MyArcGISPortalItem.Owner == _MyArcGISPortalUser.UserName)
    {
      if (sharinginfo.Access == ESRI.ArcGIS.Client.Portal.PortalAccess.Public)
      {
        // The access to the ArcGISPortalItem is Public.
        RadioButton_Public.IsChecked = true;

        // Uncheck all of the Shared (i.e. Group) CheckBoxes
        foreach (var m in _MyListOfArcGISPortalGroupExtended)
        {
          m.Selected = false;
        }
      }

      if (sharinginfo.Access == ESRI.ArcGIS.Client.Portal.PortalAccess.Organization)
      {
        // The access to the ArcGISPortalItem is the Organization.
        RadioButton_Organization.IsChecked = true;

        // Uncheck all of the Shared (i.e. Group) CheckBoxes
        foreach (var m in _MyListOfArcGISPortalGroupExtended)
        {
          m.Selected = false;
        }
      }

      if (sharinginfo.Access == ESRI.ArcGIS.Client.Portal.PortalAccess.Private)
      {
        // The access to the ArcGISPortalItem is Private.
        RadioButton_Private.IsChecked = true;

        // Uncheck all of the Shared (i.e. Group) CheckBoxes
        foreach (var m in _MyListOfArcGISPortalGroupExtended)
        {
          m.Selected = false;
        }
      }

      if (sharinginfo.Access == ESRI.ArcGIS.Client.Portal.PortalAccess.Shared)
      {
        // The access to the ArcGISPortalItem is Shared (i.e. there are Groups).
        RadioButton_Shared.IsChecked = true;

        // The user can check on various Group CheckBoxes.
        ListBox_GroupsOwned.IsEnabled = true;

        // Check on the Group CheckBoxes that are part of the Shared access.
        if (sharinginfo.GroupIds.Count() != 0)
        {
          var x = from g in _MyListOfArcGISPortalGroupExtended
            where sharinginfo.GroupIds.Any((r) => r == g.Item.Id)
            select g;
          foreach (var m in x)
          {
            m.Selected = true;
          }
        }
        else
        {
          foreach (var m in _MyListOfArcGISPortalGroupExtended)
          {
            m.Selected = false;
          }
        }
      }
    }
  }

  private void RadioButton_XXX_Click(object sender, System.Windows.RoutedEventArgs e)
  {
    // The user clicked either the Public, Organization or Private RadioButtons.

    // Unselect all of the Group CheckBoxes.
    if (RadioButton_Public.IsChecked == true)
    {
      foreach (var m in _MyListOfArcGISPortalGroupExtended)
      {
        m.Selected = false;
      }
    }

    // Disable being able to turn on Group CheckBoxes.
    ListBox_GroupsOwned.IsEnabled = false;
  }

  private void RadioButton_Shared_Click(object sender, System.Windows.RoutedEventArgs e)
  {
    // The user selected the Share (i.e. Group) RadioButton.

    // Enable the user to be able to check on various Groups.
    ListBox_GroupsOwned.IsEnabled = true;
  }

  private void ListBox_Results_SelectionChanged(object sender, System.Windows.Controls.SelectionChangedEventArgs e)
  {
    // Disable being able to select Groups. Once the user has selected the RadioButton_Shared they can check on various Group CheckBoxes.
    ListBox_GroupsOwned.IsEnabled = false;

    // Get the currently selected ArcGISPortalItem from the ListBox.
    _MyArcGISPortalItem = ListBox_Results.SelectedItem as ESRI.ArcGIS.Client.Portal.ArcGISPortalItem;

    // Make sure we have a valid ArcGISPortalItem. 
    if (_MyArcGISPortalItem == null)
    {
      return;
    }

    // Call the ArcGISPortalItem.GetSharingInfoAsync which will update the RadioButton and CheckBox controls with the most
    // current access level information.
    _MyArcGISPortalItem.GetSharingInfoAsync(CALLBACK_ArcGISPortalItem_GetSharingInfoAsync);
  }
}


// A helper class to aid with setting up binding to the controls in XAML. It basically helps track the updating of the 
// ArcGISPortalGroup with the CheckBoxes.
public class ArcGISPortalGroupExtended : System.ComponentModel.INotifyPropertyChanged
{
  private bool selected_Renamed;
  public bool Selected
  {
    get
    {
      return selected_Renamed;
    }
    set
    {
      if (selected_Renamed != value)
      {
        selected_Renamed = value;
        OnPropertyChanged("Selected");
      }
    }
  }

  private ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup item_Renamed;
  public ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup Item
  {
    get
    {
      return item_Renamed;
    }
    set
    {
      if (item_Renamed != value)
      {
        item_Renamed = value;
        OnPropertyChanged("Item");
      }
    }
  }

  public ArcGISPortalGroupExtended(ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup item)
  {
    this.Item = item;
  }

  public event System.ComponentModel.PropertyChangedEventHandler PropertyChanged;
  private void OnPropertyChanged(string property)
  {
    if (PropertyChanged != null)
    PropertyChanged(this, new System.ComponentModel.PropertyChangedEventArgs(property));
  }
}

public MainPage()
{
  InitializeComponent();

  // TODO: Provide your default values to run the example code.
  // ==========================================================

  // Provide the Url to ArcGIS OnLine (AGOL) or your ArcGIS Portal.
  TextBox_ArcGISPortalBaseUrl.Text = "http://www.arcgis.com";

  // To gain access to secured services on AGOL/Portal provide Username/Password information.
  TextBox_UserName.Text = "YourUserName";
  TextBox_Password.Text = "YourPassword";
}

private void Button1_Click(object sender, System.Windows.RoutedEventArgs e)
{
  // This sub-routine executes when the user clicks the Button. The purpose is to get general information about the
  // AGOL/Portal service (including user information if trying to access a secured service). Additionally, information
  // about ArcGISPortalItems that belong to the User is returned. 

  // Clear out any existing results before proceeding.
  TextBlock_GenericInfo.Text = "";
  ListBox_ItemInfo.Items.Clear();

  // Set the referer string of the IdentityManager using the .TokenGenrationReferer Property. 
  // This will establish the referer string that is part of the Http WebRequest Header.
  // FYI: You can view the 'Referer' header information in freeware/shareware applications like 'Fiddler'.
  // NOTE: 
  // For WPF this value could be any string. Ex: "http://www.MyWebSite.com", "http", "h", "Whatever".
  // For Silverlight this value should point to the .xap file, but any sub-string will do. Ex: "http://localhost:65121/ClientBin/SL-App.xap", 
  //   "http", "h". 
  // For Windows Phone this value should point to the application install location on the device, but any sub-string will do. Ex: 
  //   "file://Applications/Install/868A84BA-237D-4980-BF51-74E193CECCF1/Install/", "file", "f".
  ESRI.ArcGIS.Client.IdentityManager.GenerateTokenOptions myGenerateTokenOptions = null;
  myGenerateTokenOptions = new ESRI.ArcGIS.Client.IdentityManager.GenerateTokenOptions();
  myGenerateTokenOptions.Referer = "h";

  // Get the base Url for AGOL/Portal.
  string myArcGISPortalBaseUrl = TextBox_ArcGISPortalBaseUrl.Text;

  // Get the Username/Password for accessing secured AGOL/Portal services.
  string myUserName = TextBox_UserName.Text;
  string myPassword = TextBox_Password.Text;

  // Use IdentityManager to access secured AGOL/Portal services. This will cause an asynchronous call to the 
  // sub-routine called CALLBACK_IdentityManager_GenerateCredentialAsync. 
  ESRI.ArcGIS.Client.IdentityManager.Current.GenerateCredentialAsync(myArcGISPortalBaseUrl, 
    myUserName, myPassword, CALLBACK_IdentityManager_GenerateCredentialAsync, myGenerateTokenOptions);
}

private void CALLBACK_IdentityManager_GenerateCredentialAsync(ESRI.ArcGIS.Client.IdentityManager.Credential myCredential, 
  System.Exception ex)
{
  // This sub-routine executes as a result of the IdentityManager.GenerateCredentialAsync Method. 

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Create a new instance of the ArcGISPortal Class.
    ESRI.ArcGIS.Client.Portal.ArcGISPortal myArcGISPortal = new ESRI.ArcGIS.Client.Portal.ArcGISPortal();

    // Set the ArcGISPortal.Token Property to the IdentityManager.Credential long term token. Long term tokens are 
    // necessary to access secured AGOL/Portal services. 
    myArcGISPortal.Token = myCredential.Token;

    // Get the base Url of the AGOL/portal site.
    string myArcGISPortalBaseUrl = TextBox_ArcGISPortalBaseUrl.Text;

    // Add the other necessary parts to the base Url string so that information can be accessed on AGOL/Portal.
    string myArcGISPortalEnhancedUrl = myArcGISPortalBaseUrl + "/sharing/rest";

    // Make an asynchronous call to obtain information from AGOL/Portal. Upon successful completion of this call
    // you can then gain access to deeper levels of information using other ArcGISPortal asynchronous calls like:
    // .SearchGroupsAsync, .SearchItemsAsnyc, and .SearchUsersAsync. Additionally, you could also get Property 
    // information from the returned ArcGISPortal object for things like: .ArcGISPortalInfo and .CurrentUser 
    // which is a gateway to getting lots of other detailed information on AGOL/Portal. 
    myArcGISPortal.InitializeAsync(myArcGISPortalEnhancedUrl, CALLBACK_Portal_InitializeAsync);
  }
  else
  {
    // There was some problem with the IdentityManager.GenerateCredentialAsync call. Display the message to the user.
    MessageBox.Show("Could not log in. Please check credentials.");
  }
}

private void CALLBACK_Portal_InitializeAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortal myArcGISPortal, System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortal.InitializeAsync Method. 

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Get the ArcGISPortalInfo object from ArcGISPortal. 
    ESRI.ArcGIS.Client.Portal.ArcGISPortalInfo myArcGISPortalInfo = myArcGISPortal.ArcGISPortalInfo;

    // Get the ArcGISPortalInfo.PortalHostname information.
    string myPortalHostName = "";
    if (myArcGISPortalInfo != null)
    {
      myPortalHostName = myArcGISPortalInfo.PortalHostname;
    }

    // Get the ArcGISPortal.CurrentUser object. Initialize some string variables for the username and email of the
    // CurrentUser. 
    ESRI.ArcGIS.Client.Portal.ArcGISPortalUser myCurrentUser = myArcGISPortal.CurrentUser;
    string myUserName = myCurrentUser.UserName;
    string myEmail = myCurrentUser.Email;

    // Get the CurrentVerion of the AGOL/Portal server.
    string myCurrentVersion = myArcGISPortal.CurrentVersion;

    // Get the Url of the AGOL/Portal server.
    string myUrl = myArcGISPortal.Url;

    // Create a StringBuilder class to hold information obtained from the AGOL/Portal service.
    System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();
    myStringBuilder.Append("ArcGISPortalInfo.PortalHostName: " + myPortalHostName + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortal.CurrentVersion: " + myCurrentVersion + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortal.Url: " + myUrl + Environment.NewLine);
    myStringBuilder.Append("CurrentUser.UserName: " + myUserName + Environment.NewLine);
    myStringBuilder.Append("CurrentUser.Email: " + myEmail + Environment.NewLine);

    // Display the information about the AGOL/Portal server to the user.
    TextBlock_GenericInfo.Text = myStringBuilder.ToString();

    //----------------------------------------------------------------
    // Now that the ArcGISPortal.InitializeAsync has successfully been invoked, we can now get more detailed information
    // about which Items belong to the logged in User (i.e. ArcGISPortal.CurrentUser).  
    myCurrentUser.GetItemsAsync(CALLBACK_ArcGISPortalUser_GetItemsAsync);
  }
  else
  {
    // There was some problem with the ArcGISPortal.InitializeAsync call. Display the message to the user.
    MessageBox.Show("Failed to initialize" + ex.Message.ToString());
  }
}

private void CALLBACK_ArcGISPortalUser_GetItemsAsync(IEnumerable<ESRI.ArcGIS.Client.Portal.ArcGISPortalItem> IEnumberableOfArcGISPortalItem, 
  System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortalUser.GetItemsAsync Method.

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Only the valid user found via the IdentityManager will have results. Users that cannot be found via IdentityManager 
    // will come back as Nothing/null and you will need to handle accordingly.
    if (IEnumberableOfArcGISPortalItem != null)
    {
      // Create a StringBuilder class to hold User information obtained from the AGOL/Portal service. Display the 
      // number of Items found in the ListBox.
      System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();
      myStringBuilder.Append("Number of ArcGIS Portal Items: " + IEnumberableOfArcGISPortalItem.Count() + Environment.NewLine);
      ListBox_ItemInfo.Items.Add(myStringBuilder.ToString());

      // Loop through each ArcGISPortalItem object.
      foreach (ESRI.ArcGIS.Client.Portal.ArcGISPortalItem oneArcGISPortalItem in IEnumberableOfArcGISPortalItem)
      {
        // Get various ArcGISPortalItem Property information; like: the image for the Item, the Title of the Item, 
        // the long GUID type of Id for the Item, the access level (ex: Private, Public, Organizaton, etc.) of the Item,  
        // the date the Item was created, the type of Item it is (ex: shapefile, webmap, .csv file, etc.), the byte size 
        // on disk of the Item, the user access restrictions infomation of the Item, etc.
        Uri myThumbnailUri = oneArcGISPortalItem.ThumbnailUri;
        ESRI.ArcGIS.Client.Portal.PortalAccess myPortalAccess = oneArcGISPortalItem.Access;
        string myTitle = oneArcGISPortalItem.Title;
        string myId = oneArcGISPortalItem.Id;
        DateTime myCreationDate = oneArcGISPortalItem.CreationDate;
        string myTypeName = oneArcGISPortalItem.TypeName;
        int mySize = oneArcGISPortalItem.Size;
        string myAccessInformation = oneArcGISPortalItem.AccessInformation;

        // Create a new StackPanel to house the information that will be displayed to the user about each Item found.
        StackPanel myStackPanel = new StackPanel();
        myStackPanel.Orientation = Orientation.Vertical;

        if (myThumbnailUri != null)
        {
          // If the Item has an image, display it.

          // Create a new Image object, define its size and set the Uri of the Image to that for the Item. 
          Image myImage = new Image();
          myImage.Width = 75;
          myImage.Height = 75;
          myImage.Source = new System.Windows.Media.Imaging.BitmapImage(myThumbnailUri); // Silverlight has issues with .gif format images!

          // Display the image on the left in the ListBox.
          StackPanel myStackPanel2 = new StackPanel();
          myStackPanel2.Orientation = Orientation.Horizontal;
          myStackPanel2.HorizontalAlignment =System.Windows.HorizontalAlignment.Left;
          myStackPanel2.Children.Add(myImage);
          myStackPanel.Children.Add(myStackPanel2);
        }
        else
        {
          // The Item does not have an image logo defined. 

          // Inform the application user that no Item image is availble for display. 
          TextBlock myTextBlock_ArcGISPortalUser_ThumbnailImage = new TextBlock();
          myTextBlock_ArcGISPortalUser_ThumbnailImage.Text = "[NO THUMBNAIL IMAGE AVAILABLE]";
          myStackPanel.Children.Add(myTextBlock_ArcGISPortalUser_ThumbnailImage);
        }

        // Display the title of the Item. 
        TextBlock myTextBlock_ArcGISPortalItem_Title = new TextBlock();
        myTextBlock_ArcGISPortalItem_Title.Text = "ArcGISPortalItem.Title: " + myTitle;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_Title);

        // Display the long GUID type of Id for the Item.
        TextBlock myTextBlock_ArcGISPortalItem_Id = new TextBlock();
        myTextBlock_ArcGISPortalItem_Id.Text = "ArcGISPortalItem.Id: " + myId;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_Id);

        // Display the access level for the Item (ex: Private, Public, Organization, etc.).
        TextBlock myTextBlock_ArcGISPortalItem_PortalAccess = new TextBlock();
        myTextBlock_ArcGISPortalItem_PortalAccess.Text = "ArcGISPortalItem.PortalAccess: " + myPortalAccess.ToString();
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_PortalAccess);

        // Display the date the Item was created.
        TextBlock myTextBlock_ArcGISPortalItem_CreationDate = new TextBlock();
        myTextBlock_ArcGISPortalItem_CreationDate.Text = "ArcGISPortalItem.CreationDate: " + myCreationDate.ToString();
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_CreationDate);

        // Display the type of Item it is (ex: shapefile, webmap, .csv file, etc.).  
        TextBlock myTextBlock_ArcGISPortalItem_TypeName = new TextBlock();
        myTextBlock_ArcGISPortalItem_TypeName.Text = "ArcGISPortalItem.TypeName: " + myTypeName;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_TypeName);

        // Display the byte size on disk of the Item. 
        TextBlock myTextBlock_ArcGISPortalItem_Size = new TextBlock();
        myTextBlock_ArcGISPortalItem_Size.Text = "ArcGISPortalItem.Size: " + mySize;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_Size);

        // Display the user access restrictions infomation of the Item. 
        TextBlock myTextBlock_ArcGISPortalItem_AccessInformation = new TextBlock();
        myTextBlock_ArcGISPortalItem_AccessInformation.Text = "ArcGISPortalItem.AccessInformation: " + myAccessInformation;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalItem_AccessInformation);

        // Add the various display objects about the User in the ListBox.
        ListBox_ItemInfo.Items.Add(myStackPanel);
      }
    }
  }
  else
  {
    // There was some problem with the ArcGISPortalUser.GetItemsAsync call. Display the message to the user.
    MessageBox.Show("Failed to initialize" + ex.Message.ToString());
  }
}

public MainPage()
{
  InitializeComponent();

  // TODO: Provide your default values to run the example code.
  // ==========================================================

  // Provide the Url to ArcGIS OnLine (AGOL) or your ArcGIS Portal.
  TextBox_ArcGISPortalBaseUrl.Text = "http://www.arcgis.com";

  // To gain access to secured services on AGOL/Portal provide Username/Password information.
  TextBox_UserName.Text = "YourUserName";
  TextBox_Password.Text = "YourPassword";
}

private void Button1_Click(object sender, System.Windows.RoutedEventArgs e)
{
  // This sub-routine executes when the user clicks the Button. The purpose is to get general information about the
  // AGOL/Portal service (including user information if trying to access a secured service). Additionally, information
  // about ArcGISPortalGroups that a User belongs to is returned. 

  // Clear out any existing results before proceeding.
  TextBlock_GenericInfo.Text = "";
  ListBox_GroupInfo.Items.Clear();

  // Set the referer string of the IdentityManager using the .TokenGenrationReferer Property. 
  // This will establish the referer string that is part of the Http WebRequest Header.
  // FYI: You can view the 'Referer' header information in freeware/shareware applications like 'Fiddler'.
  // NOTE: 
  // For WPF this value could be any string. Ex: "http://www.MyWebSite.com", "http", "h", "Whatever".
  // For Silverlight this value should point to the .xap file, but any sub-string will do. Ex: "http://localhost:65121/ClientBin/SL-App.xap", 
  //   "http", "h". 
  // For Windows Phone this value should point to the application install location on the device, but any sub-string will do. Ex: 
  //   "file://Applications/Install/868A84BA-237D-4980-BF51-74E193CECCF1/Install/", "file", "f".
  ESRI.ArcGIS.Client.IdentityManager.GenerateTokenOptions myGenerateTokenOptions = null;
  myGenerateTokenOptions = new ESRI.ArcGIS.Client.IdentityManager.GenerateTokenOptions();
  myGenerateTokenOptions.Referer = "h";

  // Get the base Url for AGOL/Portal.
  string myArcGISPortalBaseUrl = TextBox_ArcGISPortalBaseUrl.Text;

  // Get the Username/Password for accessing secured AGOL/Portal services.
  string myUserName = TextBox_UserName.Text;
  string myPassword = TextBox_Password.Text;

  // Use IdentityManager to access secured AGOL/Portal services. This will cause an asynchronous call to the 
  // sub-routine called CALLBACK_IdentityManager_GenerateCredentialAsync. 
  ESRI.ArcGIS.Client.IdentityManager.Current.GenerateCredentialAsync(myArcGISPortalBaseUrl, 
                                                                     myUserName, 
                                                                     myPassword, 
                                                                     CALLBACK_IdentityManager_GenerateCredentialAsync, 
                                                                     myGenerateTokenOptions);
}

private void CALLBACK_IdentityManager_GenerateCredentialAsync(ESRI.ArcGIS.Client.IdentityManager.Credential myCredential, 
  System.Exception ex)
{
  // This sub-routine executes as a result of the IdentityManager.GenerateCredentialAsync Method. 

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Create a new instance of the ArcGISPortal Class.
    ESRI.ArcGIS.Client.Portal.ArcGISPortal myArcGISPortal = new ESRI.ArcGIS.Client.Portal.ArcGISPortal();

    // Set the ArcGISPortal.Token Property to the IdentityManager.Credential long term token. Long term tokens are 
    // necessary to access secured AGOL/Portal services. 
    myArcGISPortal.Token = myCredential.Token;

    // Get the base Url of the AGOL/portal site.
    string myArcGISPortalBaseUrl = TextBox_ArcGISPortalBaseUrl.Text;

    // Add the other necessary parts to the base Url string so that information can be accessed on AGOL/Portal.
    string myArcGISPortalEnhancedUrl = myArcGISPortalBaseUrl + "/sharing/rest";

    // Make an asynchronous call to obtain information from AGOL/Portal. Upon successful completion of this call
    // you can then gain access to deeper levels of information using other ArcGISPortal asynchronous calls like:
    // .SearchGroupsAsync, .SearchItemsAsnyc, and .SearchUsersAsync. Additionally, you could also get Property 
    // information from the returned ArcGISPortal object for things like: .ArcGISPortalInfo and .CurrentUser 
    // which is a gateway to getting lots of other detailed information on AGOL/Portal. 
    myArcGISPortal.InitializeAsync(myArcGISPortalEnhancedUrl, CALLBACK_Portal_InitializeAsync);
  }
  else
  {
    // There was some problem with the IdentityManager.GenerateCredentialAsync call. Display the message to the user.
    MessageBox.Show("Could not log in. Please check credentials.");
  }
}

private void CALLBACK_Portal_InitializeAsync(ESRI.ArcGIS.Client.Portal.ArcGISPortal myArcGISPortal, System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortal.InitializeAsync Method. 

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Get the ArcGISPortalInfo object from ArcGISPortal. 
    ESRI.ArcGIS.Client.Portal.ArcGISPortalInfo myArcGISPortalInfo = myArcGISPortal.ArcGISPortalInfo;

    // Get the ArcGISPortalInfo.PortalHostname information.
    string myPortalHostName = "";
    if (myArcGISPortalInfo != null)
    {
      myPortalHostName = myArcGISPortalInfo.PortalHostname;
    }

    // Get the ArcGISPortal.CurrentUser object. Initialize some string variables for the username and email of the
    // CurrentUser. 
    ESRI.ArcGIS.Client.Portal.ArcGISPortalUser myCurrentUser = myArcGISPortal.CurrentUser;
    string myUserName = myCurrentUser.UserName;
    string myEmail = myCurrentUser.Email;

    // Get the CurrentVerion of the AGOL/Portal server.
    string myCurrentVersion = myArcGISPortal.CurrentVersion;

    // Get the Url of the AGOL/Portal server.
    string myUrl = myArcGISPortal.Url;

    // Create a StringBuilder class to hold information obtained from the AGOL/Portal service.
    System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();
    myStringBuilder.Append("ArcGISPortalInfo.PortalHostName: " + myPortalHostName + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortal.CurrentVersion: " + myCurrentVersion + Environment.NewLine);
    myStringBuilder.Append("ArcGISPortal.Url: " + myUrl + Environment.NewLine);
    myStringBuilder.Append("CurrentUser.UserName: " + myUserName + Environment.NewLine);
    myStringBuilder.Append("CurrentUser.Email: " + myEmail + Environment.NewLine);

    // Display the information about the AGOL/Portal server to the user.
    TextBlock_GenericInfo.Text = myStringBuilder.ToString();

    //----------------------------------------------------------------
    // Now that the ArcGISPortal.InitializeAsync has successfully been invoked, we can now get more detailed information
    // about which Groups the logged in User (i.e. ArcGISPortal.CurrentUser) belongs to.  
    myCurrentUser.GetGroupsAsync(CALLBACK_ArcGISPortalUser_GetGroupsAsync);
  }
  else
  {
    // There was some problem with the ArcGISPortal.InitializeAsync call. Display the message to the user.
    MessageBox.Show("Failed to initialize" + ex.Message.ToString());
  }
}

private void CALLBACK_ArcGISPortalUser_GetGroupsAsync(IEnumerable<ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup> IEnumberableOfArcGISPortalGroup, 
  System.Exception ex)
{
  // This sub-routine executes as a result of the ArcGISPortalUser.GetGroupsAsync Method.

  // Ensure there was not a problem in the returned results.
  if (ex == null)
  {
    // Only the valid user found via the IdentityManager will have results. Users that cannot be found via IdentityManager 
    // will come back as Nothing/null and you will need to handle accordingly.
    if (IEnumberableOfArcGISPortalGroup != null)
    {
      // Create a StringBuilder class to hold User information obtained from the AGOL/Portal service. Display the 
      // number of Groups found in the ListBox.
      System.Text.StringBuilder myStringBuilder = new System.Text.StringBuilder();
      myStringBuilder.Append("Number of ArcGIS Portal Groups: " + IEnumberableOfArcGISPortalGroup.Count() + Environment.NewLine);
      ListBox_GroupInfo.Items.Add(myStringBuilder.ToString());

      // Loop through each ArcGISPortalGroup object.
      foreach (ESRI.ArcGIS.Client.Portal.ArcGISPortalGroup oneArcGISPortalGroup in IEnumberableOfArcGISPortalGroup)
      {
        // Get various ArcGISPortalGroup Property information. Like: the image for the Group, the access level for the
        // Group (ex: Private, Public, Organization, etc.), the owner of the Group (it might not be the Current logged in user), 
        // the snippet giving a brief description of the Group, the Title of the Group, the Description of the Group, 
        // the searchable tags for the Group, the long GUID type of Id for the Group, the date the Group was created, 
        // the date the Group information was last modifed, etc.
        Uri myThumbnailUri = oneArcGISPortalGroup.ThumbnailUri;
        ESRI.ArcGIS.Client.Portal.PortalAccess myPortalAccess = (ESRI.ArcGIS.Client.Portal.PortalAccess)oneArcGISPortalGroup.Access;
        string myOwner = oneArcGISPortalGroup.Owner;
        string mySnippet = oneArcGISPortalGroup.Snippet;
        string myTitle = oneArcGISPortalGroup.Title;
        string myDescription = oneArcGISPortalGroup.Description;
        IEnumerable<string> myTags = oneArcGISPortalGroup.Tags;
        string myId = oneArcGISPortalGroup.Id;
        DateTime myCreationDate = oneArcGISPortalGroup.CreationDate;
        DateTime myModificationDate = oneArcGISPortalGroup.ModificationDate;

        // Create a new StackPanel to house the information that will be displayed to the user about each Group found.
        StackPanel myStackPanel = new StackPanel();
        myStackPanel.Orientation = Orientation.Vertical;

        if (myThumbnailUri != null)
        {
          // If the Group has an image, display it.

          // Create a new Image object, define its size and set the Uri of the Image to that for the Group. 
          Image myImage = new Image();
          myImage.Width = 50;
          myImage.Height = 50;
          myImage.Source = new System.Windows.Media.Imaging.BitmapImage(myThumbnailUri); // Silverlight has issues with .gif format images!

          // Display the image on the left in the ListBox.
          StackPanel myStackPanel2 = new StackPanel();
          myStackPanel2.Orientation = Orientation.Horizontal;
          myStackPanel2.HorizontalAlignment = System.Windows.HorizontalAlignment.Left;
          myStackPanel2.Children.Add(myImage);
          myStackPanel.Children.Add(myStackPanel2);
        }
        else
        {
          // The Group does not have an image logo defined. 

          // Inform the application user that no Group image is availble for display. 
          TextBlock myTextBlock_ArcGISPortalUser_ThumbnailImage = new TextBlock();
          myTextBlock_ArcGISPortalUser_ThumbnailImage.Text = "[NO THUMBNAIL IMAGE AVAILABLE]";
          myStackPanel.Children.Add(myTextBlock_ArcGISPortalUser_ThumbnailImage);
        }

        // Display the access level for the Group (ex: Private, Public, Organization, etc.).
        TextBlock myTextBlock_ArcGISPortalGroup_PortalAccess = new TextBlock();
        myTextBlock_ArcGISPortalGroup_PortalAccess.Text = "ArcGISPortalGroup.PortalAccess: " + myPortalAccess.ToString();
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_PortalAccess);

        // Display the owner of the Group (it might not be the Current logged in user). 
        TextBlock myTextBlock_ArcGISPortalGroup_Owner = new TextBlock();
        myTextBlock_ArcGISPortalGroup_Owner.Text = "ArcGISPortalGroup.Owner: " + myOwner;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Owner);

        // Display the description of the Group.
        TextBlock myTextBlock_ArcGISPortalGroup_Description = new TextBlock();
        myTextBlock_ArcGISPortalGroup_Description.Text = "ArcGISPortalGroup.Description: " + myDescription;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Description);

        // Display the snippet giving a brief description of the Group. 
        TextBlock myTextBlock_ArcGISPortalGroup_Snippet = new TextBlock();
        myTextBlock_ArcGISPortalGroup_Snippet.Text = "ArcGISPortalGroup.Snippet: " + mySnippet;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Snippet);

        // Display the title of the Group. 
        TextBlock myTextBlock_ArcGISPortalGroup_Title = new TextBlock();
        myTextBlock_ArcGISPortalGroup_Title.Text = "ArcGISPortalGroup.Title: " + myTitle;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Title);

        // Display the long GUID type of Id for the Group.
        TextBlock myTextBlock_ArcGISPortalGroup_Id = new TextBlock();
        myTextBlock_ArcGISPortalGroup_Id.Text = "ArcGISPortalGroup.Id: " + myId;
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Id);

        // Display the date the Group was created.
        TextBlock myTextBlock_ArcGISPortalGroup_CreationDate = new TextBlock();
        myTextBlock_ArcGISPortalGroup_CreationDate.Text = "ArcGISPortalGroup.CreationDate: " + myCreationDate.ToString();
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_CreationDate);

        // Display the date the Group information was last modifed.
        TextBlock myTextBlock_ArcGISPortalGroup_ModificationDate = new TextBlock();
        myTextBlock_ArcGISPortalGroup_ModificationDate.Text = "ArcGISPortalGroup.ModificationDate: " + myModificationDate.ToString();
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_ModificationDate);

        // Display the searchable tags for the Group. TODO: you could loop through each tag rather than just getting the first one.
        TextBlock myTextBlock_ArcGISPortalGroup_Tags = new TextBlock();
        myTextBlock_ArcGISPortalGroup_Tags.Text = "ArcGISPortalGroup.Tags: " + myTags.First();
        myStackPanel.Children.Add(myTextBlock_ArcGISPortalGroup_Tags);

        // Add the various display objects about the User in the ListBox.
        ListBox_GroupInfo.Items.Add(myStackPanel);
      }
    }
  }
  else
  {
    // There was some problem with the ArcGISPortalUser.GetGroupsAsync call. Display the message to the user.
    MessageBox.Show("Failed to initialize" + ex.Message.ToString());
  }
}

